INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (3, N'Splatoon 3', CAST(N'2022-09-08T00:00:00.000' AS DateTime), 1, N'Enter 4-on-4* ink-slinging battles in this colorful action shooter packed with style and attitude. As a squid-like Inkling, quickly cover your surroundings (and opponents) in ink with wild weaponry and swim through your own color to sneak and splat. Dive into the fresh fun with family and friends and make waves as a team. Get splatted by an opponent? No sweat! The goal in Turf War is to cover the most ground, so respawn and jump back into the inky action.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000046395/94a4095cda06c4d85c637d1af451979f9933302b6b17174d97c45de7a68584a2', 1, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (4, N'Pokemon Legends: Arceus', CAST(N'2022-01-28T00:00:00.000' AS DateTime), 1, N'Get ready for a new kind of grand, Pokémon adventure in Pokémon™ Legends: Arceus, a new game from Game Freak that blends action and exploration with the RPG roots of the Pokémon series.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000039945/dcb496d7cf954c7eb51ab2e5d0c27918fb7f055e50f4e902135bd4a70a44b491', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (5, N'Pokemon Violet', CAST(N'2022-11-18T00:00:00.000' AS DateTime), 1, N'Catch, battle, and train Pokémon in the Paldea Region, a vast land filled with lakes, towering peaks, wastelands, small towns, and sprawling cities. Explore a wide-open world at your own pace and traverse land, water, and air by riding on a form-shifting Legendary Pokémon—Koraidon in Pokémon Scarlet and Miraidon in Pokémon Violet. Choose either Sprigatito, Fuecoco, or Quaxly, to be your first partner Pokémon before setting off on your journey through Paldea.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000053971/842b2784d91520d41a947dec17fac116fec889bb1f1db4023615af8429dae00d', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (6, N'Pokemon Scarlet', CAST(N'2022-11-18T00:00:00.000' AS DateTime), 1, N'Catch, battle, and train Pokémon in the Paldea Region, a vast land filled with lakes, towering peaks, wastelands, small towns, and sprawling cities. Explore a wide-open world at your own pace and traverse land, water, and air by riding on a form-shifting Legendary Pokémon—Koraidon in Pokémon Scarlet and Miraidon in Pokémon Violet. Choose either Sprigatito, Fuecoco, or Quaxly, to be your first partner Pokémon before setting off on your journey through Paldea.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000053966/849c234de8df7265201d26d9d72f88eed3f32438d3dca12fc135beb4c3befc85', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (7, N'Kirby and the Forgotten Land', CAST(N'2022-03-25T00:00:00.000' AS DateTime), 1, N'Float off on an all-new adventure as the powerful puffball, Kirby. Explore in 3D stages as you discover a mysterious world with abandoned structures from a past civilization—like a shopping mall?! Copy enemies’ abilities like the new Drill and Ranger and use them to attack, explore your surroundings, and save the kidnapped Waddle Dees from the ferocious Beast Pack alongside the mysterious Elfilin. Hope you’re hungry for an unforgettable adventure!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000046405/d1cf83dea46094b85247f40ca30ea4557730a319be0bfe544a4fa929144cf6c2', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (8, N'Mario + Rabbids Sparks of Hope', CAST(N'2022-10-20T00:00:00.000' AS DateTime), 1, N'Team up with Mario, Luigi, Princess Peach, Rabbid Peach, Rabbid Luigi, and their friends on a galactic journey to defeat a malevolent entity and save your Spark companions. Explore planets throughout the galaxy as you uncover mysterious secrets and compelling quests!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000035797/f3dc6aa2e25ab6d713ac5da17e90ce676dfadf6a0b8fefb37b8b5159d89fc36c', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (9, N'Nintendo Switch Sports', CAST(N'2022-04-29T00:00:00.000' AS DateTime), 1, N'Swing, kick, spike, and bowl your way to victory in Nintendo Switch Sports - a sports collection that will get the whole family moving. Get moving with Soccer, Volleyball, Bowling, Tennis, Badminton, and Chambara (swordplay) using Joy-Con controllers! Controls are intuitive, so you can hit the court (or lanes, or field, or arena) and get started. With controlled motions you can curve your bowling ball, add spin to a tennis shot, or even use a Joy-Con with the included Leg Strap accessory to kick the ball in a Soccer Shoot-Out. Family and friends can join in on the fun on the same system* or online**.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000049931/cf8adb2c74522b91412eb40b9d77216e8f8436fc4e63731e0dc262e6666930eb', NULL, CAST(49.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (10, N'Bayonetta 3', CAST(N'2022-10-28T00:00:00.000' AS DateTime), 1, N'Strut through multiple locations in an all-new, over-the-top climax action game! Bayonetta must face a mysterious man-made evil using her signature guns, time-slowing Witch Time ability, and demonic darlings. Shoot, stomp, and slam your way through multiple locations, meeting other wicked witches along the way.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000046400/0c47748b3a6f1b3a9fce87886c3dae2680f9990e27b2b6c8b029b6133f072477', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (11, N'Xenoblade Chronicles 3', CAST(N'2022-07-29T00:00:00.000' AS DateTime), 1, N'Join Noah and Mio, members of the two opposing nations of Keves and Agnus, on a heartfelt journey through a warring world with a dark secret. Traverse massive, fantastical landscapes and master seamless real-time RPG combat as you expose the true enemy pulling the strings.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000053336/e933b48650b33b355e9cf2583da5c94b77180e40fb02d050041083dd62f4df39', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (12, N'LEGO Star Wars: The Skywalker Saga', CAST(N'2022-04-05T00:00:00.000' AS DateTime), 1, N'The galaxy is yours in LEGO® Star Wars™: The Skywalker Saga. Experience memorable moments and nonstop action from all nine Skywalker saga films reimagined with signature LEGO humor.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/en_US/games/switch/l/lego-star-wars-the-skywalker-saga-switch/hero', NULL, CAST(69.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (13, N'Pokemon Brilliant Diamond', CAST(N'2021-11-19T00:00:00.000' AS DateTime), 1, N'Welcome to the Sinnoh region! Rich in nature and with mighty Mount Coronet at its heart, Sinnoh is a land of many myths passed down through the ages. You’ll choose either Turtwig, Chimchar, or Piplup to be your first partner Pokémon and then set off on your journey to try to become the Champion of the Pokémon League. Along the way, you’ll run into the mysterious organization Team Galactic, and be able to encounter the Legendary Pokémon Dialga.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000039950/37fdb03bef333221200ace5554c0d43e17ab547d8c3285d444d71808ee52f83a', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (14, N'New Pokemon Snap', CAST(N'2021-04-30T00:00:00.000' AS DateTime), 1, N'Seek out and take in-game photographs of Pokémon in their native environments in the New Pokémon Snap™ game, only for the Nintendo Switch™ system! Snap photos from the NEO-ONE as you you encounter and research lively wild Pokémon. You might see unexpected expressions or behaviors—Pokémon patrolling their territory, playing, or lurking in out-of-the-way spots.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000037989/00bd7efe46ab2b3ff0774172a5d4f21a5b2f467b3e324557ce1e9a9ae12c1d3b', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (16, N'Monster Hunter Rise', CAST(N'2021-03-26T00:00:00.000' AS DateTime), 1, N'The critically acclaimed action-RPG series returns to the Nintendo Switch™!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/en_US/games/switch/m/monster-hunter-rise-switch/hero', NULL, CAST(39.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (17, N'Mario Party Superstars', CAST(N'2021-10-29T00:00:00.000' AS DateTime), 1, N'Roll your dice and race to get the most stars (and sabotage your opponents) on 5 game boards. Each board is filled with treats and tricks—plant flowers (and Piranha Plants) on the delectable Peach’s Birthday Cake board or go intergalactic on the Space Land board. With minigames, star-stealing ghosts, and the troublemaking Bowser, you can turn the tables on your friends and family in an instant. Want to play with far-off friends and family? All modes can be played online*!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000042934/ae43ce2e25afbc4c069c2920075ead01a84f02dfe55b87876268409f6ccc0304', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (18, N'Metroid Dread', CAST(N'2021-10-08T00:00:00.000' AS DateTime), 1, N'Upon investigating a mysterious transmission on Planet ZDR, Samus faces a mysterious foe that traps her in this dangerous world. The remote planet has been overrun by vicious alien lifeforms and murderous robots called E.M.M.I. Hunt or be hunted as you make your way through a labyrinth of enemies in Samus’ most intense side-scrolling adventure yet.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000042924/4f2c683f0196210ec212a2ab8bf6952223c0b88e827b820953407a2ba61c9cb2', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (19, N'Animal Crossing: New Horizons', CAST(N'2020-03-20T00:00:00.000' AS DateTime), 1, N'Escape to a deserted island and create your own paradise as you explore, create, and customize in the Animal Crossing: New Horizons game. Your island getaway has a wealth of natural resources that can be used to craft everything from tools to creature comforts. You can hunt down insects at the crack of dawn, decorate your paradise throughout the day, or enjoy sunset on the beach while fishing in the ocean. The time of day and season match real life, so each day on your island is a chance to check in and find new surprises all year round.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000027619/9989957eae3a6b545194c42fec2071675c34aadacd65e6b33fdfe7b3b6a86c3a', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (20, N'Paper Mario: The Origami King', CAST(N'2020-07-17T00:00:00.000' AS DateTime), 1, N'The kingdom has been ravaged by an origami menace! Join Mario and his new partner, Olivia, as they battle evil Folded Soldiers, repair the damaged landscape, and try to free Princess Peach’s castle from the clutches of King Olly in this comedy-filled adventure, only on the Nintendo Switch™ system.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000029726/7f079a1e3927ea4752fb448ea7f2ccbd86933f5483ad9dd6de23ec96ed4df70b', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (23, N'Clubhouse Games: 51 Worldwide Classics', CAST(N'2020-06-05T00:00:00.000' AS DateTime), 1, N'Play and discover 51 board games, tabletop games, and more all in one package—Clubhouse Games™: 51 Worldwide Classics. This diverse collection includes games from all over the world across multiple genres, from familiar favorites like Chess to international hits like Mancala that have been around for 100s of years!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/en_US/games/switch/c/clubhouse-games-51-worldwide-classics-switch/hero', NULL, CAST(39.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (24, N'Hyrule Warriors: Age of Calamity', CAST(N'2020-11-20T00:00:00.000' AS DateTime), 1, N'Join the struggle that brought Hyrule to its knees. Learn more about Zelda, the four Champions, the King of Hyrule and more through dramatic cutscenes as they try to save the kingdom from Calamity. The Hyrule Warriors: Age of Calamity game is the only way to see firsthand what happened 100 years ago.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000034760/1123ea0e4079a8aaa32187dcaa94b8ef407e028f9b6b8d5ff944bd750f1ce347', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (25, N'Astral Chain', CAST(N'2019-08-30T00:00:00.000' AS DateTime), 1, N'The few survivors of a massive global disaster gather together in a futuristic megacity. Now it’s your job to protect them against otherworldly invaders–but you’re not alone as a member of a special police task force equipped with sentient armaments called Legions. In this brand-new action game from PlatinumGames, control the protagonist and a Legion simultaneously to chain stylish combos. Change Legions on the fly to vary your style and unleash a variety of abilities. The last remnants of humanity depend on you, and to save the world, you must investigate its dark side. Explore a near-future cyberpunk metropolis, solving crimes and seeing how the last remnants of humanity struggle to survive. As you save the world from extradimensional invaders called chimeras, you’ll also interact with citizens, question suspects, and team up with members of your task force to solve cases. Utilizing the abilities of Legions will be necessary in these investigations; you may need to track the scent of a missing person or subdue aggressive criminals! Beyond this last land left to humanity lies the Astral Plane, the alternate dimension from which the chimeras invade. As you explore its labyrinthine corridors, you must use your Legions to solve environmental puzzles and expose the secrets of this strange realm.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000018695/76f381cf2156c793d5f7a9e5c66f4d872b593d2e9d79ed8b84eb9827f32bb655', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (26, N'The Legend of Zelda: Link''s Awakening', CAST(N'2019-09-20T00:00:00.000' AS DateTime), 1, N'As Link, you awaken in a strange land away from Hyrule, where animals talk and monsters roam. To uncover the truth behind your whereabouts and rouse the legendary Wind Fish, explore Koholint Island and all its trap-ridden dungeons, reimagined in stunning detail for this new release of one of the most beloved The Legend of Zelda games. Along the way, you’ll meet a hilarious assortment of charming characters to which you’ll never want to say goodbye.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000020033/df825d5fc6747b66b051f5d3ba6a412fe625ea7878b6a10c93bb8312195682b1', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (27, N'Yoshi''s Crafted World', CAST(N'2019-03-29T00:00:00.000' AS DateTime), 1, N'Jump into a new Yoshi adventure in a world made of everyday objects—like boxes and paper cups! As Yoshi, you’ll leap up high, gulp down enemies, and set out on a treasure hunt to find all the different collectables. On the flip side, stages can be played backwards, providing new perspectives to explore and new ways to locate some of the more craftily hidden items!', N'https://assets.nintendo.com/image/upload/ar_16:9,c_lpad,w_1240/b_white/f_auto/q_auto/ncom/software/switch/70010000000734/db8a252a84301594bdc4eed1be3dbd8d3de5086bc79f6ee1f8060c9a5630101f', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (28, N'Ring Fit Adventure', CAST(N'2019-10-18T00:00:00.000' AS DateTime), 1, N'Traverse grass-swept plains by jogging in place, attack enemies with overhead shoulder presses, and refill your health meter by striking some yoga poses. Two new accessories, the Ring-Con™ and Leg Strap, measure your real-world actions and help turn them into in-game movements. With additional minigames and customizable workout routines, Ring Fit Adventure is great escape for players of all skill levels and schedules.', N'https://i.ytimg.com/vi/skBNiJd61Qw/maxresdefault.jpg', NULL, CAST(79.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (29, N'Fire Emblem: Three Houses', CAST(N'2019-07-26T00:00:00.000' AS DateTime), 1, N'War is coming to the continent of Fódlan. Here, order is maintained by the Church of Seiros, which hosts the prestigious Officer’s Academy within its headquarters. You are invited to teach one of its three mighty houses, each comprised of students brimming with personality and represented by a royal from one of three territories. As their professor, you must lead your students in their academic lives and in turn-based, tactical RPG battles wrought with strategic, new twists to overcome. Which house, and which path, will you choose?', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000007606/c499fdc86779ca95e61daed1f94288a245d196360d278138e56d44097d1a3878', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (31, N'Pokemon Shield', CAST(N'2019-11-15T00:00:00.000' AS DateTime), 1, N'A new generation of Pokémon is coming to the Nintendo Switch™ system. Begin your adventure as a Pokémon Trainer by choosing one of three new partner Pokémon: Grookey, Scorbunny, or Sobble. Then embark on a journey in the new Galar region, where you’ll challenge the troublemakers of Team Yell, while unraveling the mystery behind the Legendary Pokémon Zacian and Zamazenta! Explore the Wild Area, a vast expanse of land where the player can freely control the camera. Team up with three other players locally or online in the new multiplayer co-op Max Raid Battles* in which players will face off against gigantic and super-strong Pokémon known as Dynamax Pokémon.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000004492/6a824ace8e4b9504eb2937a1a054536d5dc2538ef8c9ef5ac7f1d9ab0f39a78a', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (32, N'Pokemon Sword', CAST(N'2019-11-15T00:00:00.000' AS DateTime), 1, N'A new generation of Pokémon is coming to the Nintendo Switch™ system. Begin your adventure as a Pokémon Trainer by choosing one of three new partner Pokémon: Grookey, Scorbunny, or Sobble. Then embark on a journey in the new Galar region, where you’ll challenge the troublemakers of Team Yell, while unraveling the mystery behind the Legendary Pokémon Zacian and Zamazenta! Explore the Wild Area, a vast expanse of land where the player can freely control the camera. Team up with three other players locally or online in the new multiplayer co-op Max Raid Battles* in which players will face off against gigantic and super-strong Pokémon known as Dynamax Pokémon.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000004496/1a7e765fe91da863330208620c5446908cc7ae47108a80a168ff65356df764db', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (33, N'Super Mario Maker 2', CAST(N'2019-06-28T00:00:00.000' AS DateTime), 1, N'Mario fans of the world, unite! Now you can play, create, and share* the side-scrolling Super Mario™ courses of your dreams in the Super Mario Maker™ 2 game, available exclusively on the Nintendo Switch™ system! Dive into the single-player Story Mode and play built-in courses to rebuild Princess Peach’s castle. Make your own courses, alone or together. And with a Nintendo Switch Online membership*, share your courses, access a near-endless supply made by others, enjoy online multiplayer, and more!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000018694/aac100206f1c50349fd2c48595e55603259ae60a4ab4eefc28e71f41efb78b97', NULL, CAST(39.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (34, N'Luigi''s Mansion 3', CAST(N'2019-10-31T00:00:00.000' AS DateTime), 1, N'Luigi’s invited to the towering Last Resort hotel, but when Mario and friends go missing, our green-clad hero will have to conquer his fears to save them! Slam, blow away, and vacuum up ghosts with the all-new Poltergust G-00, and join forces with Gooigi to overcome the puzzling contraptions and mischievous boss on each themed floor. And that’s just the Last Resort. Enter the ScareScraper for 8-player local wireless* or online** co-op gameplay.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000001620/2b166fb3197dacfde1d939acd6a976b9fbe3b1a32c54f9f0d2c8d23efb22412d', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (35, N'Super Smash Bros. Ultimate', CAST(N'2018-12-07T00:00:00.000' AS DateTime), 1, N'Gaming icons clash in the ultimate brawl you can play anytime, anywhere! Smash rivals off the stage as new characters Simon Belmont and King K. Rool join Inkling, Ridley, and every fighter in Super Smash Bros. history. Enjoy enhanced speed and combat at new stages based on the Castlevania series, Super Mario Odyssey, and more!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000012332/ac4d1fc9824876ce756406f0525d50c57ded4b2a666f6dfe40a6ac5c3563fad9', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (36, N'Super Mario Party', CAST(N'2018-10-05T00:00:00.000' AS DateTime), 1, N'Whether you’re pedaling tricycles, flipping meat, or who knows what else, you’ll use Joy-Con™ controllers in clever ways across 80 wild minigames. Some are all-out free-for-alls, others are 2 vs 2, or even 1 vs 3! Set up a private room and play your favorites and enjoy some friendly competition with friends online.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000014158/0ca41128bf40b7e0dc32a046a659ecdc69e4d7cc7a3257d6280be665d2f795b5', NULL, CAST(59.90 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (37, N'Hades', CAST(N'2021-03-19T00:00:00.000' AS DateTime), 1, N'As the immortal Prince of the Underworld, you''ll wield the powers and mythic weapons of Olympus to break free from the clutches of the god of the dead himself, while growing stronger and unraveling more of the story with each unique escape attempt.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/en_US/games/switch/h/hades-switch/hero', NULL, CAST(34.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (38, N'Celeste', CAST(N'2018-01-25T00:00:00.000' AS DateTime), 1, N'Help Madeline survive her inner demons on her journey to the top of Celeste Mountain, in this super-tight, hand-crafted platformer from the creators of multiplayer classic TowerFall.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/en_US/games/switch/c/celeste-switch/hero', NULL, CAST(19.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (39, N'Pokemon: Let''s Go, Pikachu!', CAST(N'2018-11-16T00:00:00.000' AS DateTime), 1, N'Partner up with Pikachu to catch and collect Pokémon on a rich and vibrant adventure in the Pokémon™: Let’s Go, Pikachu! game – available now on the Nintendo Switch™ system! Become the best Pokémon Trainer you can be as you battle other Trainers, Gym Leaders, and the sinister Team Rocket. Catch Pokémon in the wild using a gentle throwing motion with either a Joy-Con™ controller or a Poké Ball™ Plus accessory (sold separately), which will light up, vibrate, and make sounds to bring your adventure to life. You can also use button controls in Handheld Mode. Share your adventure with family or friends using a second Joy-Con or Poké Ball Plus. You can even connect to the Pokémon™ GO app* using a compatible smartphone to bring over Pokémon originally discovered in the Kanto region!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000000447/8b2acaf2549895635f8d706eac06b3751e56bc3a970b04a937dcdec3e87ad3bf', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (40, N'Kirby Star Allies', CAST(N'2018-03-16T00:00:00.000' AS DateTime), 1, N'When an evil threatens Planet Popstar, Kirby will need a little help from his...enemies?! By making friends out of foes, up to three** players can drop in or out of the adventure at any time. With expanded copy abilities, classic Kirby action is even deeper: combine abilities with elements such as ice or fire to create friend abilities!', N'https://cdn.contentoptimised.com/cb:xdtR~aa60/w:auto/h:auto/q:mauto/f:avif/https://www.nintendoreporters.com/storage/files/kirby-star-allies-update-nieuwe-droomvrienden-en-mode.png', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (41, N'Cuphead', CAST(N'2019-04-18T00:00:00.000' AS DateTime), 1, N'Cuphead is a classic run and gun action game heavily focused on boss battles. Inspired by cartoons of the 1930s, the visuals and audio are painstakingly created with the same techniques of the era: traditional hand drawn cel animation, watercolor backgrounds, and original jazz recordings.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000016330/d94d2186ef03c930392253c83c84af0c73b7e57cd902a526b09b4155a25930fe', NULL, CAST(19.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (42, N'Dead Cells', CAST(N'2018-08-07T00:00:00.000' AS DateTime), 1, N'Explore a vast, ever-changing island filled with secrets, branching paths and challenging foes. Silky smooth controls, fast paced combat and undeniably enjoyable game-feel make each run more addictive than the next as you progressively unlock permanent upgrades. Multiple difficulty modifiers will help you find the challenge that’s right for you as you discover the characters and lore of the island.', N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/71-5Qi560vL._AC_UF1000,1000_QL80_.jpg', NULL, CAST(31.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (43, N'Mario + Rabbids Kingdom Battle', CAST(N'2017-08-29T00:00:00.000' AS DateTime), 1, N'The Mushroom Kingdom has been torn apart by a mysterious vortex, transporting the chaotic Rabbids into this once-peaceful land. To restore order, Mario, Luigi, Princess Peach, and Yoshi must team up with a whole new crew: four Rabbids heroes! Together, they will battle with weapons through four worlds filled with combat, puzzles, and unpredictable enemies.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/en_US/games/switch/m/mario-plus-rabbids-kingdom-battle-switch/herov2', NULL, CAST(19.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (45, N'Splatoon 2', CAST(N'2017-07-21T00:00:00.000' AS DateTime), 1, N'The squid kids called Inklings are back to splat more ink and claim more turf in this colorful and chaotic 4-on-4 action shooter. For the first time, take Turf War battles on-the-go with the Nintendo Switch™ system, and use any of the console’s portable play styles for intense local multiplayer* action. Even team up for new 4-player co-op fun in Salmon Run!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000000529/b0b183a9860296016649fadb03b929411e7e5e0809af241e2e9652ebf0c5a715', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (46, N'Hollow Knight', CAST(N'2018-06-12T00:00:00.000' AS DateTime), 1, N'Hollow Knight is a classically styled 2D action adventure across a vast interconnected world. Explore twisting caverns, ancient cities and deadly wastes; battle tainted creatures and befriend bizarre bugs; and solve ancient mysteries at the kingdom''s heart.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000003208/4643fb058642335c523910f3a7910575f56372f612f7c0c9a497aaae978d3e51', NULL, CAST(14.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (47, N'Super Mario Odyssey', CAST(N'2017-10-27T00:00:00.000' AS DateTime), 1, N'Explore incredible places far from the Mushroom Kingdom as you join Mario and his new ally Cappy on a massive, globe-trotting 3D adventure. Use amazing new abilities—like the power to capture and control objects, animals, and enemies—to collect Power Moons so you can power up the Odyssey airship and save Princess Peach from Bowser’s wedding plans!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000001130/c42553b4fd0312c31e70ec7468c6c9bccd739f340152925b9600631f2d29f8b5', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (49, N'The Legend of Zelda: Breath of the Wild', CAST(N'2017-03-03T00:00:00.000' AS DateTime), 1, N'Forget everything you know about The Legend of Zelda games. Step into a world of discovery, exploration, and adventure in The Legend of Zelda: Breath of the Wild, a boundary-breaking new game in the acclaimed series. Travel across vast fields, through forests, and to mountain peaks as you discover what has become of the kingdom of Hyrule in this stunning Open-Air Adventure. Now on Nintendo Switch, your journey is freer and more open than ever. Take your system anywhere, and adventure as Link any way you like.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000000025/7137262b5a64d921e193653f8aa0b722925abc5680380ca0e18a5cfd91697f58', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (50, N'Mario Kart 8 Deluxe', CAST(N'2017-04-28T00:00:00.000' AS DateTime), 1, N'Burn rubber across Mushroom Kingdom raceways—underwater, in the sky, upside-down in zero-g, and past the finish—for the win! Get revved up for local multiplayer*, online** in-game tournaments, a revamped battle mode, and more!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000000153/de697f487a36d802dd9a5ff0341f717c8486221f2f1219b675af37aca63bc453', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (51, N'Stardew Valley', CAST(N'2017-10-05T00:00:00.000' AS DateTime), 1, N'You’ve inherited your grandfather’s old farm plot in Stardew Valley. Armed with hand-me-down tools and a few coins, you set out to begin your new life. Can you learn to live off the land and turn these overgrown fields into a thriving home? It won’t be easy. Ever since Joja Corporation came to town, the old ways of life have all but disappeared. The community center, once the town’s most vibrant hub of activity, now lies in shambles. But the valley seems full of opportunity. With a little dedication, you might just be the one to restore Stardew Valley to greatness!', N'https://www.discoazul.com/uploads/media/images/stardew-valley-switch-17.jpg', NULL, CAST(14.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (52, N'Donkey Kong Country: Tropical Freeze', CAST(N'2018-05-04T00:00:00.000' AS DateTime), 1, N'Barrel-blast into a critically acclaimed Donkey Kong adventure as this beloved franchise makes its Nintendo Switch™ debut with a banana-bunch of new features. Traverse islands packed with platforming perfection and nonstop action as the classic Kongs in the original game, or mix things up by playing the story as Funky Kong in new Funky Mode!', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000001339/d01cba67eb98fec32ac98027c07fa8d92dd52773565bfadc69f817c860e34b8a', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (53, N'Bayonetta 2', CAST(N'2018-02-16T00:00:00.000' AS DateTime), 1, N'Bayonetta’s back and more powerful than ever. Wield wild weapons and execute deadly moves—like the powerful Umbran Climax—to take out angels and demons in this breathtaking action game. You can even team up with friends in 2-player online or local wireless co-op fights (additional accessories required; sold separately.) Bayonetta is a butt-kicking, havoc-wreaking witch who wields sweet weapons like pistols, whips, hammers, flamethrowers, and poison bows. But it’s not just about brawn—it’s also about style. Bayonetta is deadly but sleek, with moves like Witch Time that slows down time itself, and the all-new Umbran Climax—a special magic attack that summons Infernal Demons to devastate enemies. The Bayonetta™ 2 game also features an online and local wireless 2-player cooperative mode where players bet halos on their performance and work together to amplify their sass, cause destruction, and score some riches.', N'https://fs-prod-cdn.nintendo-europe.com/media/images/10_share_images/games_15/nintendo_switch_4/H2x1_NSwitch_Bayonetta2_image1600w.jpg', NULL, CAST(49.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (55, N'Pikmin 3 Deluxe', CAST(N'2020-10-30T00:00:00.000' AS DateTime), 1, N'Command a capable crop of different types of Pikmin™ to strategically overcome obstacles, defeat creatures, and find food for your famished home planet! You can even bring a second player along to divvy up tasks as you explore a world that seems larger than life from a pint-sized perspective.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000005302/6917dc68ed3d3675368164e9a19d30f256de38cef36c636f9aebb3dcbeb61b19', NULL, CAST(59.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (56, N'Minecraft', CAST(N'2018-06-21T00:00:00.000' AS DateTime), 1, N'Minecraft is a game about placing blocks and going on adventures.', N'https://assets.nintendo.com/image/upload/c_fill,w_1200/q_auto:best/f_auto/dpr_2.0/ncom/software/switch/70010000000964/811461b8d1cacf1f2da791b478dccfe2a55457780364c3d5a95fbfcdd4c3086f', NULL, CAST(39.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (57, N'The Legend of Zelda: Breath of the Wild', CAST(N'2017-03-03T00:00:00.000' AS DateTime), 2, N'Explore the wilds of Hyrule any way you like Climb up towers and mountain peaks in search of new destinations, then set your own path to get there and plunge into the wilderness. Along the way, you''ll battle towering enemies, hunt wild beasts and gather ingredients for the food and elixirs you''ll need to sustain you on your journey', N'https://m.media-amazon.com/images/I/71B5c+Emz6L._SX385_.jpg', NULL, CAST(39.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (59, N'The Legend of Zelda: Twilight Princess HD', CAST(N'2016-03-04T00:00:00.000' AS DateTime), 2, N'Explore the wilds of Hyrule any way you like - Climb up towers and mountain peaks in search of new destinations, then set your own path to get there and plunge into the wilderness. Along the way, you''ll battle towering enemies, hunt wild beasts and gather ingredients for the food and elixirs you''ll need to sustain you on your journey.', N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/A1HHIwo+HYL.jpg', NULL, CAST(39.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (60, N'Paper Mario: Color Splash', CAST(N'2016-10-07T00:00:00.000' AS DateTime), 2, N'A mystery is afoot on Prism Island - and only Paper Mario can solve it by restoring the island''s vanishing colors with his new Paint Hammer and the powerful Battle Cards up his sleeve. These fresh layers of strategy, along with the hilarious action adventure gameplay the series is known for, make this one of the most colorful Paper Mario action-adventure games ever.', N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/91bXFtCdvUL.jpg', NULL, CAST(39.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (61, N'Shantae: 1/2 Genie Hero', CAST(N'2016-12-20T00:00:00.000' AS DateTime), 2, N'Shantae embarks on her first full HD adventure! When a mysterious crime wave sweeps Sequin Land, it''s up to Half-Genie Hero Shantae to save the day! Use Shantae''s devastating Hair-Whip Attack to send monsters flying, or Belly Dance to transform into a more powerful creature form! Topple the masterminds behind each criminal caper and prepare for the ultimate showdown against Shantae''s arch nemesis – the bodacious buccaneer Risky Boots!', N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/917EJfT49OL.jpg', NULL, CAST(34.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (62, N'Fast Racing Neo', CAST(N'2015-12-10T00:00:00.000' AS DateTime), 2, N'FAST Racing NEO is a game that seeks to fill the F-Zero-sized hole in every Nintendo fan''s heart. Shin''en''s racer is quite different from Nintendo''s classic franchise, but it still does a great job at giving that high speed racing fix that some Wii U owners are desiring the most.', N'https://production-gameflipusercontent.fingershock.com/us-east-1:9929d1a7-078c-4ee0-bf1e-299e97b4244d/64ef23f8-6584-4269-9ad9-3128d9dd1f6a/fc60789b-dc50-4137-87d0-5a1c3fcab24e', NULL, CAST(19.99 AS Decimal(19, 2)))
GO
INSERT [dbo].[Game] ([GameID], [GameTitle], [ReleaseDate], [ConsoleID], [GameDescription], [GameImageURL], [ESBRRatingID], [Price]) VALUES (63, N'Skylanders: SuperChargers', CAST(N'2015-09-20T00:00:00.000' AS DateTime), 2, N'Skylanders plus vehicles equals SuperChargers! Fly, swim and drive across the land as you perform insane stunts with your favorite Skylander characters.', N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/81lz4TqDolL.jpg', NULL, CAST(29.99 AS Decimal(19, 2)))
